import {useState, useEffect} from 'react'

function App() {

  const [time, setTime] = useState()
  useEffect(()=>{
    setInterval(()=>{
      const now = new Date().toLocaleTimeString()
      setTime(now)
    },1000)
  },[])
  let [hr, min, sec]  =time.split(':')
  return (
    <div>
      {hr}
    </div>
  )
}

export default App
