import {useState, useEffect} from 'react'
import './App.css';
function App() {

  const [time, setTime] = useState()
  useEffect(()=>{
    setInterval(()=>{
      const now = new Date().toLocaleTimeString()
      setTime(now)
    },1000)
  },[])
  return (
    <div className='flex justify-center ' >
    <div  className='text-red-600 max-w-[1500px] text-center text-9xl mt-72 border-2 container'>
    <p>{time}</p>
    </div>
    </div>
  )
}

export default App
